function [traj, time] = GetTrajs(data,d)%% This function returns the flow and time arrays of the date (d) in data%% (matrix that contains multiple days of flow of one cell)    traj = [];    time = [];    % Add a date column    data = [data zeros(length(data),1)];    data(:,11) = datenum(strcat('2016-03-',d));        j = 1;    for t = 0:5:1410        if j > length(data(:,3))            traj = [traj;0];            time = [time;t+1415*(str2num(d)-1)];            continue;        end        if data(j,3) > t            traj = [traj;0];            time = [time;t+1415*(str2num(d)-1)];        else            traj = [traj;data(j,5)];            time = [time;t+1415*(str2num(d)-1)];            j = j + 1;        end        endendfunction