plotdays = linspace(trainDays, numdays, numdays-trainDays); % the dates to predictfor i = 1:length(plotdays)    startind = (plotdays(i)-1)*283*(numZones+1) + 1;    endind = plotdays(i)*283*(numZones+1);    xtest = x(startind:endind,:);    ytest = y(startind:endind,:);    %timeplot = mod(time(startind:endind,:),1415);    [mu, s2] = gp(hyp2, @infExact, meanfunc, covfunc, likfunc, xtrain, ytrain, xtest);    fig = figure;    plot(ytest(1:283),'b', "LineWidth",2);    hold on;    plot(mu(1:283),'r');    xlim([0,283]);    legend("Actual Flow", "Prediction");    hold off;    %saveas(fig, strcat('03-',num2str(plotdays(i)),'.png'));end